import React from 'react'
import './Card.css'
import { Link } from 'react-router-dom'
import { differenceInHours, differenceInDays } from 'date-fns';

const Card = (props) =>  {

  const createdTime = new Date(props.createdtime);
  const now = new Date();

  const hoursDifference = differenceInHours(now, createdTime);
  const daysDifference = differenceInDays(now, createdTime);

  return (
    <div className="Card">
        <Link to={'edit/'+ props.id}></Link>
            <p> Likes: {props.likes}</p>
            <h2 className="title">{props.title}</h2>
            <p className="createdtime">
              {hoursDifference < 24
                ? `Created ${hoursDifference} hours ago`
                : `Created ${daysDifference} days ago`}
            </p>
            {props.content && <h3 className="content">{props.content}</h3>}
    </div>
  );
};

export default Card;
