import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { supabase } from '../client';

const PostPage = (props,id) => {
    const [postdetails, setPostDetails] = useState(null);
    const params = useParams();
    const [post, setPost] = useState({id: null, title: "", content: "", imageurl: "",likes: 0, comments: []});

    useEffect (() => {
        const fetchPost = async () => {
            const {data, error} = await supabase
                .from('Posts')
                .select()
                .eq('id', params.id)
                .single();
            console.log(params.id);
            console.log(data);
            setPostDetails(data);
        };
        fetchPost();
    }, [params.id]);

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    const createComment = async (event) => {
        event.preventDefault();

        await supabase
            .from('Posts')
            .update({comments: post.comments})
            .eq('id', params.id)
            .select();

            window.location = "/PostPage:id";
    }
    // const updatePost = async (event) => {
    //     event.preventDefault();
    //     await supabase
    //         .from('Posts')
    //         .update({title: post.title, content: post.content, imageurl: post.imageurl})
    //         .eq('id', params.id)
    //         .select();
        
    //     window.location = "http://localhost:3000/view";

    // const deletePost = async (event) => {
    //     event.preventDefault();
    //     await supabase
    //         .from('Posts')
    //         .delete()
    //         .eq('id', params.id)
    //         .select();
            
    //     window.location = "/";
    //}

    return (
        <div>
            {postdetails && (
                <div className="Details">
                    <h1>{postdetails.title}</h1>
                    <p>{postdetails.content}</p>
                    <img src={postdetails.imageurl} alt="postimage"/>
                    {/* <button onClick={deletePost}>Delete</button>
                    <button onClick={updatePost}>Update</button> */}
                    <br></br>
                    <Link to={`/edit/${postdetails.id}`}>Edit</Link>
                    <div>
                        <input type="text" id="comment" name="comment" placeholder="Leave a Comment Here"  onChange={handleChange} /><br />
                        <button onClick={createComment}>Comment</button>
                        <br/>
                    </div>
                </div>
            )}
        </div>
    );


}

export default PostPage;
