import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import { supabase } from '../client';

const PostPage = (props,id) => {
    const [postdetails, setPostDetails] = useState(null);
    const params = useParams();
    const [post, setPost] = useState({id: null, title: "", content: "", imageurl: "",likes: 0, comments: []});
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchPostDetails = async () => {
            const { data: postData } = await supabase
                .from('Posts')
                .select('*')
                .eq('id', params.id)
                .single();
    
            const { data: commentsData } = await supabase
                .from('Comments')
                .select('*')
                .eq('post_id', params.id);
    
            setPostDetails(postData);
            setComments(commentsData);
        }
    
        fetchPostDetails();
    }, [params.id]);

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    const createComment = async (event) => {
        event.preventDefault();
        const { data, error } = await supabase
            .from('Posts')
            .update({ comments: supabase.raw('array_append(comments, ?)', [comment]) })
            .eq('id', params.id);
    
        if (error) {
            console.error('Error adding comment:', error);
            // Handle the error appropriately in your app
        } else {
            setPostDetails(data);
        }
    }
    // const updatePost = async (event) => {
    //     event.preventDefault();
    //     await supabase
    //         .from('Posts')
    //         .update({title: post.title, content: post.content, imageurl: post.imageurl})
    //         .eq('id', params.id)
    //         .select();
        
    //     window.location = "http://localhost:3000/view";

    // const deletePost = async (event) => {
    //     event.preventDefault();
    //     await supabase
    //         .from('Posts')
    //         .delete()
    //         .eq('id', params.id)
    //         .select();
            
    //     window.location = "/";
    //}

    return (
        <div>
            {postdetails && (
                <div className="Details">
                    <h1>{postdetails.title}</h1>
                    <p>{postdetails.content}</p>
                    <img src={postdetails.imageurl} alt="postimage"/>
                    {/* <button onClick={deletePost}>Delete</button>
                    <button onClick={updatePost}>Update</button> */}
                    <br></br>
                    <Link to={`/edit/${postdetails.id}`}>Edit</Link>
                    <div>
                        <input type="text" id="comment" name="comment" placeholder="Leave a Comment Here"  onChange={handleChange} /><br />
                        <button onClick={createComment}>Comment</button>
                        <br/>
                    </div>
                    <div>
                    {comments && comments.map((comment, index) => (
                        <p key={index}>{comment.text}</p>
                    ))}
                </div>
                </div>
            )}
        </div>
    );


}

export default PostPage;
